import React, { useState, useEffect } from "react";
import Navbar from '../components/Navbar/Navbar';
// import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';

// import SeattleBoatMap from "../components/Images/Seattle-Boat-Map.jpg";
import VesselList from "../components/VesselList/VesselList"
// import VesselDummyData from "../utils/VesselDummyData";
import MarineTrafficMap from "../components/MarineTrafficMap/MarineTrafficMap"
import SampleMap from '../components/SampleMap/SampleMap';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'
import API from "../utils/API";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Link } from "react-router-dom";
import { List, ListItem } from "../components/List/List"

import '../App.css';







function mapPage(props) {

    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const [traffic, setTraffic] = useState([]);
    const [trip, setTrip] = useState([])


    const [searchTerm, setSearchTerm] = useState({
        startLocation: "",
        endLocation: "",
        // startingDate: new Date(),
        // endingDate: new Date()
    })

    const [tripDates, setTripDates] = useState({
        startingDate: new Date(),
        endingDate: new Date()
    })



    const handleInputChange = (event, date) => {
        event.preventDefault();
        const value = event.target.value
        setSearchTerm({
            ...searchTerm, [event.target.name]: value
        });
        console.log(value)

    }

    const handleChangeDate = (date) => {
        setTripDates({
            startingDate: date,
            endingDate: date
        })
        console.log(date)
    }

    const onSubmitHandler = (event) => {
        // Prevent browser refreshing after form submission
        event.preventDefault();
        setSearchTerm('')
    }

    function waveHello() {
        console.log('ðŸ‘‹');
    }

    useEffect(() => {
        loadTraffic()
    }, [])

    useEffect(() => {
        loadTrips()
    }, [])

    function loadTrips() {
        API.getTrips()
            .then(res => {
                console.log(res)
                setTrip(res.data)
            })
            .catch(err => console.log(err));
    };

    function loadTraffic() {
        API.getTraffic()
            .then(res => {
                console.log(res)
                setTraffic(res.data)
            })
            .catch(err => console.log(err));
    };

    function toasty() {
        console.log("cheers to that")
        toast("Your trip has been saved", {
            position: toast.POSITION.TOP_CENTER
        });

    }

    function handleFormSubmit(index) {
        console.log(index);
        waveHello();
        API.saveTrip({
            start_destination: searchTerm.startLocation,
            end_destination: searchTerm.endLocation,
            start_sail_date: tripDates.startingDate,
            end_sail_date: tripDates.endingDate
        })
        setSearchTerm("")
    }

    function deleteTrip(id) {
        API.deleteTrip(id)
            .then(res => loadTrips())
            .catch(err => console.log(err));
    }


    return (

        <div style={{textAlign:"center"}}>



            <Navbar />
            {/* <div style={{
            backgroundImage: `url(${SeattleBoatMap})`,
            backgroundPosition: 'center',
            backgroundSize: 'cover',
            backgroundRepeat: 'no-repeat',
            width: "100%",
            height: "760px",

            }}>

        </div> */}


<Row>
    <Col>
<MarineTrafficMap />
</Col>

<Col>
<h1>Plan your trip here</h1>

<Button><Link
    to="/plantrip"
    className={window.location.pathname === "/plantrip" ? "nav-link active" : "nav-link"}
            >
    <h1 style={{color:"white"}}>Plan your voyage</h1>
    </Link></Button>
    </Col>
</Row>


            {/* <p>
                <VesselList />
            </p> */}

        </div>
    )
}

export default mapPage;
