import React, { useState, useEffect } from "react";
import Navbar from '../components/Navbar/Navbar';
import FormCheck from 'react-bootstrap/FormCheck';
import FormControl from 'react-bootstrap/FormControl';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Calendar from 'react-calendar';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import ImageBackground from "../components/Images/Seattle-Boats.jpg";


function PlanTrip() {

    const [startDate, setStartDate] = useState(Date.now());
    const [endDate, setEndDate] = useState(Date.now());


    return (

        <div style={{
            backgroundImage: `url(${ImageBackground})`,
            backgroundPosition: 'center',
            backgroundSize: 'cover',
            backgroundRepeat: 'no-repeat',
            width: "100%",
            height: "1000px",
        }}>           

        <Navbar />

            <h1>Plan your trip here</h1>

            <Container>

            <Form>
                <Form.Group controlId="formBasicEmail">
                    <Form.Label>Start</Form.Label>
                    <Form.Control type="email" placeholder="Start location" />
                    <Form.Text className="text-muted">
                    </Form.Text>
                </Form.Group>

                <Form.Group controlId="formBasicPassword">
                    <Form.Label>End</Form.Label>
                    <Form.Control type="password" placeholder="End location" />
                </Form.Group>

                <Container>
                    <Row>
                        <Col>
                            <DatePicker
                                selected={startDate}
                                onChange={date => setStartDate(date)}
                                selectsStart
                                startDate={startDate}
                                endDate={endDate}
                            />
                        </Col>
                        <Col>
                            <DatePicker
                                selected={endDate}
                                onChange={date => setEndDate(date)}
                                selectsEnd
                                startDate={startDate}
                                endDate={endDate}
                                minDate={startDate}
                            />
                        </Col>
                    </Row>
                </Container>

                <Form.Group controlId="formBasicCheckbox">
                    <Form.Check type="checkbox" label="Save Trip" />
                </Form.Group>
                <Button variant="primary" type="submit">
                    Submit
                </Button>
            </Form>

            </Container>

        </div>
    )

}

export default PlanTrip;
